x = 5
y = 10
z = x < y  # z wordt True omdat x kleiner is dan y
print(z)  # output: True

a = True
b = False
c = a and b  # c wordt False omdat a en b niet allebei True zijn
print(c)  # output: False

d = not a  # d wordt False omdat not a het tegenovergestelde van True is
print(d)  # output: False
