# declaring float variables
x = 3.1415
y = 2.71828

# performing calculations with floats
result = x + y
print(result) # output: 5.85978

result = x * y
print(result) # output: 8.53972922

result = y / x
print(result) # output: 0.8652600833568355

# comparing floats
if x < y:
    print("x is less than y")
elif x > y:
    print("x is greater than y")
else:
    print("x and y are equal")
